mapper.select:
	/*com.dev.company.mapper.admin.UserDAO.userList*/
	SELECT 
		seq, name, id, phone, address, 
		department, position, reg_date 
	FROM 
		tb_user
	WHERE
		status > 0

			AND
			(	
					name LIKE CONCAT('%', #{keyword}, '%')
				OR
				 	id LIKE CONCAT('%', #{keyword}, '%')
				OR
				 	phone LIKE CONCAT('%', #{keyword}, '%')
		 	)


			AND ${searchType} LIKE CONCAT('%', #{keyword}, '%')


			AND department = #{department}


			AND position = #{position}


            AND DATE(reg_date) >= #{startDate}


            AND DATE(reg_date) <= #{finishDate}


	LIMIT 
		#{pageIndex}, #{pageSize}

	/*com.dev.company.mapper.admin.UserDAO.userListCnt*/
	SELECT count(*) FROM 
		tb_user
	WHERE
		status > 0

			AND
			(	
					name LIKE CONCAT('%', #{keyword}, '%')
				OR
				 	id LIKE CONCAT('%', #{keyword}, '%')
				OR
				 	phone LIKE CONCAT('%', #{keyword}, '%')
		 	)


			AND ${searchType} LIKE CONCAT('%', #{keyword}, '%')


			AND department = #{department}


			AND position = #{position}


            AND DATE(reg_date) >= #{startDate}


            AND DATE(reg_date) <= #{finishDate}

/*com.dev.company.mapper.admin.UserDAO.userList*/
SELECT count(*) FROM 
	tb_user
WHERE
	id = #{id}
